          error: null,
          isLoaded: false,
          items: []
        };
      }
      componentDidMount(){
        const script = document.createElement("script");

        script.src = `js/content.js`;
        script.src = `js/seed.js`;
        script.async = true;

        document.body.appendChild(script);
