import React, { Component } from 'react'
//import './js/seed.js'


export default class Interest extends Component {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
        this.getTheApi = this.getTheApi.bind(this);
      }

      

            render() {
              
                return (
                  <div>
                     <div className="col-md-3 col-sm-6 col-xs-12">
                        <div className="info-box">
                          <span className="info-box-icon bg-red"><i className="fa fa-star-o" /></span>
                          <div className="info-box-content">
                            <span className="info-box-text">{this.props.id}</span>
                            <span className="info-box-number">{this.props.topic}</span>
                          </div>
                          {/* /.info-box-content */}
                        </div>
                        {/* /.info-box */}
                      </div>

                  </div>
                )
    }
}


class InterestList extends React.Component {
    render() {
     // const Seed = document.createElement("script");
      //Seed.src = `js/seed.js`;
      //document.body.appendChild(Seed);
      const interestComponents = Seed.interests.map((interest) =>
      <Interest
        id = {interest.Interest_ID}
        topic= {interest.Topic}
      />
    );
    return (
      <div className='ui unstackable items'>
        {interestComponents}
      </div>
    );
    }
  }
