import React, { Component } from 'react'

const Interest = ({ body }) => {
  return (
    <div>
      {body.map(interest => {
        const { Interest_ID, Topic } = interest;
        return (
          <div key={Interest_ID}>
            <h2>{Topic}</h2>
            <hr />
          </div>
        );
      })}
    </div>
  );
};

export default class InterestList extends Component { //App
  state = {
    isLoading: true,
    interests: [],
    error: null
  }
  fetchInterest(){
    fetch("http://localhost:46204/odata/Interests")
    .then(res => res.json())
    .then(
      (result) => {
        //console.log(result.value)
        this.setState({interests:result.value});
        interests = result.value;
        console.log('Interests:')
        console.log(interests)
      })
    }
  
    componentDidMount() {
      this.fetchInterest();
    }
      render() {
        
          return (
            <div>
                 <h1>React Fetch - Blog</h1>
                 <hr />
                  {
                  !isLoading ? 
                  Object.keys(interests).map(key => <Interest key={key} body={interests[key]} />) 
                  : <h3>Loading...</h3>
                  }
            </div>
          )
  }
}
