import React, { Component } from 'react'

const Interest = ({ body }) => {
  return (
    <div>
      {body.map(interest => {
        const { Interest_ID, Topic } = interest;
        return (
          <div key={Interest_ID}>
            <h2>{Topic}</h2>
            <hr />
          </div>
        );
      })}
    </div>
  );
};

export default class InterestList extends Component { //App
  
   state = {
    isLoading: true,
    interests: [],
    error: null
  }
    fetchInterests() {
      // The API where we're fetching data from
      fetch(`http://localhost:46204/odata/Interests`)
        // We get a response and receive the data in JSON format...
        .then(response => response.json())
        // ...then we update the state of our application
        .then(
          data =>
            this.setState({
              interests: data.value,
              isLoading: false,
            })
        )
        // If we catch errors instead of a response, let's update the app
        .catch(error => this.setState({ error, isLoading: false }));
    }
  
    componentDidMount() {
      this.fetchInterests();
    }
      render() {
        const { isLoading, posts, error } = this.state;
          return (
            <div>
                 <h1>React Fetch - Blog</h1>
                 <hr />
                  {
                  !isLoading ? 
                  Object.keys(interests).map(key => <Interest key={key} body={interests[key]} />) 
                  : <h3>Loading...</h3>
                  }
            </div>
          )
  }
}
